
public class Ex00 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int  a,b,c;
		int result;
		
		a = 15431;
		b = 646542;
		c = 1234;
		
		result = a + b / c;
		System.out.println(a + " + " + b + " / " + c + " = " + result);
		//a+b/c의 값//
	
		result = a * b / c;
		System.out.println(a + " * " + b + " / " + c + " = " + result);
		//a*b/c의 값//
		
		result = a+b+c;
		System.out.println(a+" + "+b+ " + "+c+" = "+result);
		//a+b+c의 값//
		
			
	}

}
